import axios from 'axios';

axios.defaults.baseURL = `https://pixabay.com/api/?q=cat&page=1&key=your_key&image_type=photo&orientation=horizontal&per_page=12`;



export const fetchPictureWithQuery = async searchQuery => {
    const response = axios.get(`q=${searchQuery}&image_type=photo`);
    console.log(response.data.hits);
  return response.data.hits;
};

export default {
  fetchPictureWithQuery,
};
