import { ImageGallery } from './ImageGallery/ImageGallery';
import { SearchBar } from './SearchBar/SearchBar';
import React, { Component } from 'react';
import axios from 'axios';
// import { render } from '@testing-library/react';
// https://pixabay.com/api/?key=30662426-21982097d0559eebc608a0eec&q=yellow+flowers&image_type=photo

axios.defaults.baseURL = `https://pixabay.com/api/?q=cat&page=1&key=30662426-21982097d0559eebc608a0eec&`;

const GalleryList = ({ pictures }) => (
  <ul>
    {pictures.map(({ id, pageUrl, tags }) => (
      <li key={id}>
        <a href={pageUrl} target="_blank" rel="noreferrer noopener">
          {tags}
        </a>
      </li>
    ))}
  </ul>
);

export class App extends Component {
  state = {
    pictures: [],
  };

  async componentDidMount() {
    console.log('did mount');
    const response = await axios.get(
      'image_type=photo&orientation=horizontal&per_page=12'
    );
    console.log(response.data.hits);
    this.setState({ pictures: response.data.hits });
  }

  
  
  render() {
    const { pictures } = this.state;

    return (
      <>
        <SearchBar />
        <ImageGallery />
        <GalleryList pictures={pictures} />
      </>
    );
  }
}
