import { ImageGallery } from './ImageGallery/ImageGallery';
import { SearchBar } from './SearchBar/SearchBar';
import React, { Component } from 'react';
import axios from 'axios';
import { CirclesWithBar } from 'react-loader-spinner';

axios.defaults.baseURL = `https://pixabay.com/api/?q=cat&page=1&key=30662426-21982097d0559eebc608a0eec&`;

export class App extends Component {
  state = {
    pictures: [],
    picture: '',
    isLoading: false,
    error: null,
  };

  async componentDidMount() {
    this.setState({ isLoading: true });
    //  try {
    //   const response = await axios.get("/search?query=react");
    //   this.setState({ articles: response.data.hits });
    // } catch (error) {
    //   this.setState({ error });
    // } finally {
    //   this.setState({ isLoading: false });
    // }

    try {
      const response = await axios.get(
        `image_type=${this.state.picture}&per_page=12`
      );
      this.setState({
        pictures: response.data.hits,
        isLoading: false,
      });
    } catch (error) {
      this.setState({ error });
    } finally {
      this.setState({
        pictures: response.data.hits,
        isLoading: false,
      });
    }
  }

  searchPicture = value => {
    console.log(`search bar`);
    this.setState({ picture: value });
  };

  render() {
    const { pictures, isLoading } = this.state;

    return (
      <>
        <SearchBar onSubmit={this.searchPicture} />
        {/* {pictures.length > 0 ? <ImageGallery pictures={pictures} /> : null}; */}
        {isLoading ? (
          <CirclesWithBar
            height="100"
            width="100"
            color="#4fa94d"
            outerCircleColor="#4fa94d"
            innerCircleColor="#4fa94d"
            barColor="#4fa94d"
            ariaLabel="circles-with-bar-loading"
            wrapperStyle={{}}
            wrapperClass=""
            visible={true}
          />
        ) : (
          <ImageGallery pictures={pictures} />
        )}
      </>
    );
  }
}
