import axios from 'axios';

const key = '30662426-21982097d0559eebc608a0eec';

axios.defaults.baseURL = `https://pixabay.com/api/?key=${key}&`;

export const fetchPictureWithQuery = async searchQuery => {
//   console.log(searchQuery);
  const response = await axios.get(`q=${searchQuery}&image_type=photo&per_page=12`);
  console.log(response.data.hits);
  return response.data.hits;
};



function fetchPhotos(nextName, nextPage, perPage) {
  return fetch(
    `https://pixabay.com/api/?q=${nextName}&page=${nextPage}&key=30662426-21982097d0559eebc608a0eec&image_type=photo&orientation=horizontal&per_page=${perPage}`
  ).then(response => {
    if (response.ok) {
      return response.json();
    }
  });
}

const api = {
  fetchPhotos,
};

export default api;
